var DateCalendar; 
function changeDate(d){ 
  if (d == "") { 
    d = DateCalendar; 
  } 
  var da = d.split(' '); 
  day = "<strong class='date_day'>"+parseFloat(da[1].replace(/,/g, ''))+"</strong>"; 		
  month = "<strong class='date_month'>"+da[0].slice(0,3)+"</strong>"; 
  year = "<strong class='date_year'>"+da[2]+"</strong>"; 
  document.write(month+day+year); 
  DateCalendar = d; 
} 
